options.getOption:
// Pega a primeira série de dados retornada pela sua query
const series = context.panel.data.series[0];

// Se não houver dados, retorna um objeto vazio para não dar erro
if (!series || !series.fields) {
  return {};
}

// Encontra os campos 'word', 'frequency' e 'sentiment' nos dados
const wordField = series.fields.find(f => f.name === 'word');
const frequencyField = series.fields.find(f => f.name === 'frequency');
const sentimentField = series.fields.find(f => f.name === 'sentiment'); // NOVO: Pega o campo de sentimento

// Se algum dos campos essenciais não for encontrado, retorna um objeto vazio
if (!wordField || !frequencyField || !sentimentField) {
  return {};
}

// Transforma os dados no formato que o gráfico precisa
const data = [];
for (let i = 0; i < series.length; i++) {
  const sentiment = sentimentField.values[i];
  let color;

  // NOVO: Define a cor com base no valor do sentimento
  if (sentiment === 'positive') {
    color = '#2ca02c'; // Verde
  } else if (sentiment === 'negative') {
    color = '#d62728'; // Vermelho
  } else {
    color = '#808080'; // Cinza para 'neutral' ou qualquer outro valor
  }

  data.push({
    name: wordField.values[i],
    value: frequencyField.values[i],
    // NOVO: Adiciona a informação de estilo (cor) para cada palavra individualmente
    textStyle: {
      color: color
    }
  });
}

// Retorna o objeto de configuração completo do ECharts
return {
  // ALTERADO: Tooltip formatado para melhor visualização
  tooltip: {
    formatter: function (params) {
      return `Palavra: <strong>${params.name}</strong><br/>Frequência: <strong>${params.value}</strong>`;
    }
  },
  series: [{
    type: 'wordCloud',
    shape: 'circle',

    // ALTERADO: Aumenta o tamanho mínimo da fonte e o espaçamento
    gridSize: 15,
    sizeRange: [20, 95],

    // ALTERADO: Remove a rotação das palavras (todas ficam na horizontal)
    rotationRange: [0, 0],

    drawOutOfBound: false,

    // O textStyle geral é herdado, mas a cor é sobrescrita pela cor individual de cada palavra
    textStyle: {
      fontFamily: 'sans-serif',
      fontWeight: 'bold',
    },

    emphasis: {
      focus: 'self',
      textStyle: {
        textShadowBlur: 10,
        textShadowColor: '#333'
      }
    },

    // Passa os dados formatados para o gráfico
    data: data
  }]
};

